print(list(map(lambda a, m = input('msg >'), k = input('key >'), e = int(input('applications of the key >')): ''.join(list(map(lambda x: chr(x + 65), list(map(lambda x: (x[0] + (e*x[1])) % 26, list(zip(list(map(lambda x: ord(x) - 65, m.upper())), [list(map(lambda x: ord(x) - 65, k.upper()))[i % len(list(map(lambda x: ord(x) - 65, k.upper()))) ] for i in range(len(list(map(lambda x: ord(x) - 65, m.upper()))))]))))))), ['anystring']))[0])
